mapper.select:
       select qw.Data_ID,qw.RegDate,qw.Queue_No,qw.Queue_Pos,qw.Customer_Name,qw.Customer_No,qw.Customer_ID,qw.Status_Type,qw.Customer_Type,tw.StatusTypeName,sg.SrvGroup_ID,sg.SrvGroup_Code,sg.SrvGroup_Name,ws.WS_ID,ws.WS_Name,sf.Staff_Code,sf.Staff_Name from Basic_Queue_Work qw
		left join Basic_SrvGroup sg on qw.SrvGroup_ID=sg.SrvGroup_ID
		left join Basic_WorkStation ws on qw.WS_ID=ws.WS_ID
		left join Basic_StaffInfo sf on qw.Staff_ID=sf.Staff_ID
		left join StatusType_Work tw on qw.Status_Type=tw.StatusTypeId


              and  Customer_ID=#{Customer}


              and  Queue_No=#{QueueNo}


              and   Status_Type=#{StatusType}


       order by Customer_Type,Data_ID asc
